import { Collection } from "../../util/collection.js";
/** Returns a list of guild channel objects. */
export async function getChannels(bot, guildId) {
    const results = await bot.rest.runMethod(bot.rest, "GET", bot.constants.routes.GUILD_CHANNELS(guildId));
    return new Collection(results.map((result) => {
        const channel = bot.transformers.channel(bot, { channel: result, guildId });
        return [channel.id, channel];
    }));
}
