import { Collection } from "../../util/collection.js";
/** Get pinned messages in this channel. */
export async function getPins(bot, channelId) {
    const results = await bot.rest.runMethod(bot.rest, "GET", bot.constants.routes.CHANNEL_PINS(channelId));
    return new Collection(results.map((result) => {
        const message = bot.transformers.message(bot, result);
        return [message.id, message];
    }));
}
