import { Collection } from "../../../util/collection.js";
/** Returns thread members objects that are members of the thread. */
export async function getThreadMembers(bot, threadId) {
    const results = await bot.rest.runMethod(bot.rest, "GET", bot.constants.routes.THREAD_MEMBERS(threadId));
    return new Collection(results.map((result) => {
        const member = bot.transformers.threadMember(bot, result);
        return [member.id, member];
    }));
}
