import { Collection } from "../../util/collection.js";
/**
 * Returns a list of emojis for the given guild.
 */
export async function getEmojis(bot, guildId) {
    const results = await bot.rest.runMethod(bot.rest, "GET", bot.constants.routes.GUILD_EMOJIS(guildId));
    return new Collection(results.map((result) => {
        const emoji = bot.transformers.emoji(bot, result);
        return [emoji.id, emoji];
    }));
}
