import { Collection } from "../../../util/collection.js";
/** Get a list of all rules currently configured for guild. */
export async function getAutomodRules(bot, guildId) {
    const results = await bot.rest.runMethod(bot.rest, "GET", bot.constants.routes.AUTOMOD_RULES(guildId));
    return new Collection(results.map((result) => {
        const rule = bot.transformers.automodRule(bot, result);
        return [rule.id, rule];
    }));
}
