import { Collection } from "../../util/collection.js";
/** Returns an array of voice regions that can be used when creating servers. */
export async function getAvailableVoiceRegions(bot) {
    const results = await bot.rest.runMethod(bot.rest, "GET", bot.constants.routes.VOICE_REGIONS());
    return new Collection(results.map((result) => {
        const region = bot.transformers.voiceRegion(bot, result);
        return [region.id, region];
    }));
}
