import { Collection } from "../../util/collection.js";
/** Returns a list of role objects for the guild.
 *
 * ⚠️ **If you need this, you are probably doing something wrong. This is not intended for use. Your roles will be cached in your guild.**
 */
export async function getRoles(bot, guildId) {
    const results = await bot.rest.runMethod(bot.rest, "GET", bot.constants.routes.GUILD_ROLES(guildId));
    return new Collection(results.map((result) => {
        const role = bot.transformers.role(bot, { role: result, guildId });
        return [role.id, role];
    }));
}
