import { Collection } from "../../util/collection.js";
/** Returns an array of templates. Requires the `MANAGE_GUILD` permission. */
export async function getGuildTemplates(bot, guildId) {
    const results = await bot.rest.runMethod(bot.rest, "GET", bot.constants.routes.GUILD_TEMPLATES(guildId));
    return new Collection(results.map((result) => {
        const template = bot.transformers.template(bot, result);
        return [template.code, template];
    }));
}
